sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.claims = [];

	var gr = new GlideRecord('x_1296763_expense_reimbursement_claim');
	gr.addQuery('sys_created_by', gs.getUserName());
	gr.query();


	while(gr.next()) {
		var claimData = {};
		claimData.number = gr.getValue('number');
		claimData.totalAmount = gr.getValue('total_amount');
		claimData.reportName = gr.getValue('report_name');
		claimData.policy = gr.getValue('policy_type');
		claimData.currency = gr.getValue('currency');

		findExpenses(claimData);

		data.claims.push(claimData);
	}

	function findExpenses(claimData) {	
		var expenseQuery = new GlideRecord('x_1296763_expense_reimbursement_expense');
		expenseQuery.addQuery('claim.number', claimData.number);
		expenseQuery.query();

		var expenses = [];

		while (expenseQuery.next()) {
			var expense = {};
			expense.number = expenseQuery.getValue('number');
			expense.amount = expenseQuery.getValue('amount');
			expense.paymentType = expenseQuery.getValue('payment_type');
			expense.currency = expenseQuery.getValue('currency');
			expense.vendorName = expenseQuery.getValue('vendor_name');
			 gs.addInfoMessage(expense.number);
			expenses.push(expense);
		}

		claimData.expenses = expenses;
	}
})();
