sys_script_client.script:
function onLoad() {
    var expenseID = g_form.getValue('number');

    var request = new GlideAjax('GetClaimDetails');
    request.addParam('sysparm_name', 'getStatus');
    request.addParam('sysparm_expense_id', expenseID);
    request.getXML(onResponse);

    function onResponse(response) {
        var status = response.responseXML.documentElement.getAttribute('answer');
		g_form.addInfoMessage(status);
		if (status && status !== 'draft') {
			g_form.addInfoMessage(status);
			disableFields();
		}
    }

    function disableFields() {
        var fieldsToDisable = [
            'vendor_name',
            'expense_type',
            'amount',
            'transaction_date',
            'comment',
            'receipt_status',
			'payment_type',
			'claim',
        ];

        fieldsToDisable.forEach(function(field) {
            g_form.setDisabled(field, true);
        });
    }

}
