sys_script_client.script:
function onLoad() {
    if (!g_form.isNewRecord()) return;

    var gd = new GlideAjax("GetUserInfo");
    gd.addParam('sysparm_name', 'getDepartment');
	gd.addParam('sysparm_user_id', g_user.userID);
    gd.getXML(onDepartment);

    var gc = new GlideAjax("GetUserInfo");
    gc.addParam('sysparm_name', 'getCompany');
	gc.addParam('sysparm_user_id', g_user.userID);
    gc.getXML(onCompany);

    function onDepartment(r) {
        var departmentId = r.responseXML.documentElement.getAttribute('answer');
        g_form.addInfoMessage("Department: " + departmentId);
        if (departmentId) {
            g_form.setValue('department', departmentId);
        }
    }

    function onCompany(r) {
        var companyId = r.responseXML.documentElement.getAttribute('answer');
        g_form.addInfoMessage("Company: " + companyId);

        if (companyId) {
            g_form.setValue('company', departmentId);
        }
    }

}
