sys_script_client.script:
function onLoad() {
 disableAllFields();

 function disableAllFields() {
     var fieldsToDisable = [
    'report_name',
    'report_date',
    'policy_type',
    'company',
    'report_name',
    'department',
    'comment',
    'business_purpose',
   ];

   fieldsToDisable.forEach(function (field) {
     g_form.setDisabled(field, true);
   });
 }
}
